mapper.select:
SELECT unid, uuid, serverUuid, typeId, name, serviceName, port, manageURL, stateCommand, installPath, stopCommand, startCommand, restartCommand, backupCommand, logPath, create_time, update_time, status FROM t_sm_service_config
1 = 1
AND unid = #{unid}
AND uuid = #{uuid}
AND serverUuid = #{serverUuid}
AND typeId = #{typeId}
AND name = #{name}
AND serviceName = #{serviceName}
AND status = #{status}
ORDER BY unid DESC

SELECT unid, uuid, serverUuid, typeId, name, serviceName, port, manageURL, stateCommand, installPath, stopCommand, startCommand, restartCommand, backupCommand, logPath, create_time, update_time, status FROM t_sm_service_config
1 = 1
AND unid = #{unid}
AND uuid = #{uuid}
AND serverUuid = #{serverUuid}
AND typeId = #{typeId}
AND name = #{name}
AND serviceName = #{serviceName}
AND status = #{status}
ORDER BY unid DESC LIMIT 1
